mapper.select:
SELECT a.*, m.nickname AS 'extra__writer', b.name AS board_name FROM article a INNER JOIN `member` m ON a.memberId = m.id INNER JOIN board b ON a.boardId = b.id WHERE 1
AND a.boardId = #{boardId}
ORDER BY a.id DESC

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id};

UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id};

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id};

mapper.update:
UPDATE article
SET goodReactionPoint = goodReactionPoint + 1
WHERE id = #{id}

UPDATE article
SET goodReactionPoint = goodReactionPoint - 1
WHERE id = #{relId}
