mapper.select:
SELECT c.*, ca.name AS category_name, r.step1 AS 'region_name'
FROM club c
INNER JOIN category ca
ON c.categoryId = ca.id
INNER JOIN region r
ON c.areacode = r.areacode
WHERE 1

	AND c.categoryId = #{categoryId}



	AND (c.name LIKE CONCAT('%',#{searchKeyword},'%')
	OR
	c.purpose LIKE CONCAT('%',#{searchKeyword},'%'))



	AND r.step1 = #{step1}


	AND r.step2 = #{step2}


	AND r.step3 = #{step3}

ORDER BY c.id DESC

	LIMIT #{limitFrom}, #{limitTake}

SELECT COUNT(c.id)
FROM club c
INNER JOIN region r
ON c.areacode = r.areacode
WHERE 1

	AND c.categoryId = #{categoryId}


	AND (c.name LIKE CONCAT('%',#{searchKeyword},'%')
	OR
	c.purpose LIKE CONCAT('%',#{searchKeyword},'%'))


	AND r.step1 = #{step1}


	AND r.step2 = #{step2}


	AND r.step3 = #{step3}

SELECT COUNT(*)
FROM member_club
WHERE clubId = #{id}
AND memberId = #{actorId};

SELECT *
FROM member_club
WHERE memberId = #{memberId}
AND clubId = #{clubId};
